var fs = require('fs');
var mysql  = require('mysql').createConnection({
	host     : '127.0.0.1',
	user     : 'root',
	connectionLimit : 100,
	password : '',
	database : 'presensi_online',
	port		: 3306,
});
module.exports = {
  mySQLQuery : function(query){
		return new Promise(function(resolve, reject) {
			try {
				mysql.query(query.text, query.
					placeholder, function(err, rows, fields) {
					if (err) {
						// mysql.end();
						return reject(err);
					} else {
						// mysql.end();
						return resolve(rows);
					}
				});
			} catch (err) {
				return reject(err);
			}
		});
  }
}
